#include <iostream>#include <map>#include <vector>#include <algorithm>int main(){    int n, x;    std::cin >> n >> x;    std::vector<int> a(n);    for(int i = 0; i < n; ++i){        std::cin >> a.at(i);    }    int count = 0;    for(int i = 0; i < n - 1; ++i){        if(x < a.at(i) + a.at(i + 1)){            count += a.at(i) + a.at(i + 1) - x;        }    }    std::cout << count << std::endl;    return 0;}