#include <iostream>#include <vector>int main(){    std::string s;    std::cin >> s;    std::reverse(s.begin(), s.end());    std::string key;    int index = 0;    bool flg = true;    while(s.size() > index && flg){        for(int i = 5; i <= 7; ++i){            if(i > s.size()){                flg = false;                break;            }            key = s.substr(index, i);            if("maerd" == key || "remaerd" == key || "esare" == key || "resare" == key){                index = index + i;                break;            }else if(7 == i){                flg = false;                break;            }        }    }    if(flg){        std::cout << "YES" << std::endl;    }else{        std::cout << "NO" << std::endl;    }}