#include <iostream>#include <cmath>#include <vector>#include <algorithm>int main(){    int n;    std::cin >> n;    std::vector<int> vec(n);    for(int i = 0; i < n; ++i){        std::cin >> vec.at(i);    }    int count = 0;    int key = vec.size() - 1;    bool flg = true;    while(key >= 0){        if(0 == key){            if(1 != std::count(vec.begin(), vec.end(), key)){                flg = false;                break;            }        }else if(2 != std::count(vec.begin(), vec.end(), key)){            flg = false;            break;        }        key -= 2;    }    if(flg){        std::cout << std::pow(2, vec.size() / 2) << std::endl;    }else{        std::cout << 0 << std::endl;    }    return 0;}