#include <iostream>#include <map>#include <vector>#include <algorithm>int main(){    int n, k;    std::cin >> n >> k;    int a, b;    std::vector<int> vec;    for(int i = 0; i < n; ++i){        std::cin >> a >> b;        int size = vec.size();        vec.resize(size + b);        for(int j = 0; j < b; ++j){            vec.at(size + j) = a;        }    }    std::sort(vec.begin(), vec.end());    std::cout << vec.at(k - 1) << std::endl;    return 0;}