#include <iostream>#include <vector>int main(){    long n;    std::cin >> n;    std::vector<long> a(n);    for (long i = 0; i < n; ++i){        std::cin >> a.at(i);    }    bool flg = false;    int index = 1;    long counter = 0;    std::vector<long> history;    while(!flg){//        std::cout << "index: " << index << std::endl;        history.push_back(index);        index = a.at(index - 1);        ++counter;        if(2 == index){            flg = true;        }else if(history.end() != std::find(history.begin(), history.end(), index)){            counter = -1;            flg = true;        }    }    std::cout << counter << std::endl;    return 0;}