#include <iostream>#include <vector>#include <algorithm>int main(){    int n;    std::cin >> n;    std::vector<std::vector<int>> pos(1, std::vector<int>(3, 0));    std::vector<int> posBuf(3);    for(int i = 0; i < n; ++i){        std::cin >> posBuf.at(0) >> posBuf.at(1) >> posBuf.at(2);        pos.push_back(posBuf);    }    bool flg = false;    for(int i = 0; i <= n; ++i){        if(n == i){            break;         }        int term = pos.at(i + 1).at(0) - pos.at(i).at(0);        int distX = abs(pos.at(i + 1).at(1) - pos.at(i).at(1));        int distY = abs(pos.at(i + 1).at(2) - pos.at(i).at(2));        int gridDist = distX + distY;        int limit = term/2;        for(int j = 0; j <= limit; ++j){            if(2 * j + term%2 == gridDist){                flg = true;                break;            }else if(2 * j + term%2 != gridDist && j == limit){                flg = false;            }        }        if(!flg){            break;        }    }    if(flg){        std::cout << "Yes" << std::endl;    }else{        std::cout << "No" << std::endl;    }}