#include <vector>#include <iostream>using namespace std;int main(){    vector<int> coins(3);    int totalPattern = 1;    for(int i = 0; i < 3; ++i){        cin >> coins[i];        totalPattern *= 1 + coins[i];    }    int counter = 0;    vector<int> amounts(totalPattern);    for(int i = 0; i <= coins[0]; ++i){        for(int j = 0; i <= coins[1]; ++j){            for(int k = 0; k <= coins[2]; ++k){                amounts[counter] = 500*i + 100*j + 50*k;                ++counter;            }        }    }    int target;    cin >> target;    cout << count(amounts.begin(), amounts.end(), target) << endl;}