#include <iostream>#include <map>#include <vector>#include <algorithm>#include <numeric>int main(){    int n;    std::cin >> n;    std::vector<int> a1(n);    std::vector<int> a2(n);    for(int i = 0; i < n; ++i){        std::cin >> a1.at(i);        a2.at(i) = abs(a1.at(i) - (i + 1));    }    int b = 0;    b = std::accumulate(a2.begin(), a2.end(), 0) / n;    int ans = 0;    for(int i = 0; i < n; ++i){        ans += abs(a2.at(i) - b);    }    std::cout << ans << std::endl;    return 0;}