#include <iostream>#include <vector>int main(){    int a, b, c;    std::cin >> a >> b >> c;    int min;    if(a <= b){        min = a;    }else{        min = b;    }    std::vector<int> buf{0};    for(int i = 1; i <= min; ++i){        if(0 == a%i){            if(0 == b%i){                buf.push_back(i);            }        }    }    std::cout << buf.at(buf.size() - c) << std::endl;    return 0;}