#include <iostream>#include <map>#include <vector>#include <algorithm>int main(){    int n, q;    std::cin >> n >> q;    std::string s;    std::cin >> s;    for(int i = 0; i < s.size() - 1; ++i){        if("AC" == s.substr(i, 2)){            s.at(i) = 'a';        }    }    int start, end, count;    for(int i = 0; i < q; ++i){        std::cin >> start >> end;        count = std::count(s.begin() + start - 1, s.begin() + end - 1, 'a');        std::cout << count << std::endl;;    }    return 0;}