#include <iostream>#include <map>#include <vector>#include <algorithm>int main(){    std::vector<int> a(5);    for(int i = 0; i < 5; ++i){        std::cin >> a.at(i);    }    int k;    std::cin >> k;    bool flg = true;    for(int i = 0; i < 5; ++i){        for(int j = 0; j < 5; ++j){            if(abs(a.at(i) - a.at(j)) > k){                flg = false;                break;            }        }    }    if(flg){        std::cout << "Yay!" << std::endl;    }else{        std::cout << ":(" << std::endl;    }    return 0;}