#include <iostream>#include <map>#include <vector>#include <algorithm>int main(){    int n;    std::cin >> n;    std::vector<int> transport(5);    for(int i = 0; i < 5; ++i){        std::cin >> transport.at(i);    }     int timeLimit = 100000000;    std::vector<std::vector<int>> dp(6, std::vector<int>(timeLimit, 0));    dp.at(0).at(0) = n;    int people;    int count = 0;    for(int i = 1; i < timeLimit; ++i){        for(int j = 0; j < 5; ++j){            people = std::min(transport.at(j), dp.at(j).at(i - 1));            dp.at(j).at(i) = dp.at(j).at(i - 1) - people;            dp.at(j + 1).at(i) = dp.at(j + 1).at(i - 1) + people;        }        ++count;        if(n <= dp.at(5).at(i)){            break;        }    }    std::cout << count << std::endl;    return 0;}