#include <iostream>#include <cmath>#include <vector>#include <algorithm>int main(){    int n;    std::cin >> n;    std::vector<int> vec;    int count = 0;    while(0 != n){        vec.push_back(n % 1);        n /= 2;    }        std::cout << vec.size() << std::endl;    for(int i = 0; i < vec.size(); ++i){        if(0 == vec.at(i)){            std::cout << std::pow(2, i) * vec.at(i) << std::endl;        }    }    return 0;}