#include <iostream>#include <map>#include <vector>#include <algorithm>#include <boost/lexical_cast.hpp>int main(){    std::string s;    std::cin >> s;    int i = 1;    int count = 1;    while(i != s.size()){        std::cout << i << std::endl;        if(s.at(i - 1) == s.at(i)){            ++count;            i += 1;        }else if(i = s.size() - 1){            s.push_back(boost::lexical_cast<char>(count));        }else{            s.insert(s.begin() + i, boost::lexical_cast<char>(count));            i += 2;            count = 1;        }    }        s.erase(std::unique(s.begin(), s.end()), s.end());    std::cout << s << std::endl;    return 0;}