#include <iostream>#include <vector>int main(){    int n;    std::cin >> n;    int val;    std::vector<int> route;    for(int i = 0; i < 2; ++i){        std::cin >> val;        route.push_back(val);    }    int k;    std::cin >> k;    for(int i = 0; i < k; ++i){        std::cin >> val;        route.push_back(val);    }    std::vector<int> buffer = route;    std::unique(buffer.begin(), buffer.begin());    route.erase(std::unique(buffer.begin(), buffer.end()), buffer.end());    bool flg = true;    for(int i = 0; i < buffer.size(); ++i){        if(1 != std::count(route.begin(), route.end(), buffer.at(i))){            flg = false;            break;        }    }    if(flg){        std::cout << "YES" << std::endl;    }else{        std::cout << "NO" << std::endl;    }    return 0;}