#include <iostream>#include <cmath>int main(){    int n, a, b;    std::cin >> n >> a >> b;    int sum = 0;    int buf;    int ans = 0;       for(int i = 1; i <= n; ++i){        buf = i;        sum = 0;        sum += buf/10000;        buf -= (buf/10000)*10000;        sum += buf/1000;        buf -= (buf/1000)*1000;        sum += buf/100;        buf -= (buf/100)*100;        sum += buf/10;        buf -= (buf/10)*10;        sum += buf/1;        buf -= (buf/1)*1;        if(a <= sum && sum <= b){            ans += i;        }    }        std::cout << ans << std::endl;}