#include <iostream>#include <vector>int main(){    int n;    std::cin >> n;    std::vector<int> p(n);    std::cin >> p.at(0) >> p.at(1);    int count = 0;    for(int i = 2; i < n; ++i){        std::cin >> p.at(i);        if(p.at(i - 2) < p.at(i - 1) && p.at(i - 1) < p.at(i)){            ++count;        }else if(p.at(i - 2) > p.at(i - 1) && p.at(i - 1) > p.at(i)){            ++count;        }    }    std::cout << count << std::endl;    return 0;}