#include <iostream>#include <vector>#include <cmath>int main(){    int n, d;    std::cin >> n >> d;    std::vector<std::vector<int>> x(n, std::vector<int>(d));    for(int i = 0; i < n; ++i){        for(int j = 0; j < d; ++j){            std::cin >> x.at(i).at(j);        }    }    float val = 0;    int count = 0;    for(int i = 0; i < n; ++i){        for(int j = i; j < n; ++j){            for(int k = 0; k < d; ++k){                val += std::pow(x.at(i).at(k) - x.at(j).at(k), 2);            }            val = std::pow(val, 0.5);            if(0.0 != val && 0.0 == val - (int)val){                ++count;            }            val = 0;        }    }    std::cout << count << std::endl;    return 0;}