#include <algorithm>#include <iostream>#include <iomanip>#include <vector>int main(){    int n, k;    std::cin >> n >> k;    std::vector<double> r(n);    for(int i = 0; i < n; ++i){        std::cin >> r.at(i);    }    std::sort(r.begin(), r.end());    double  ans = 0;    for(int i = n - k; i < n; ++i){        ans = (ans + r.at(i)) / 2;    }    std::cout << std::fixed << std::setprecision(7) << ans << std::endl;    return 0;}