#include <iostream>#include <iterator>#include <vector>int main(){    std::vector<int> vec(5);    for(int i = 0; i < 5; ++i){        std::cin >> vec.at(i);    }    int count = 0;    std::vector<int> sums;    for(int i = 0; i < 5; ++i){        for(int j = i + 1; j < 5; ++j){            for(int k = j + 1; k < 5; ++k){                sums.push_back(vec.at(i) + vec.at(j) + vec.at(k));            }        }    }    std::sort(sums.begin(), sums.end());    std::cout << *(sums.rbegin() + 2) << std::endl;    return 0;}