#include <iostream>#include <vector>#include <cmath>int main(){    int n;    std::cin >> n;    std::vector<int> a(n);    for(int i = 0; i < n; ++i){        std::cin >> a.at(i);    }    int min = *std::min_element(a.begin(), a.end());        int max = *std::max_element(a.begin(), a.end());        std::vector<int> ans;    int sum = 0;    for(int i = min; i <= max; ++i){        for(int j = 0; j < n; ++j){            sum += std::pow(i - a.at(j), 2);                 }        ans.push_back(sum);        sum = 0;    }    std::cout << *std::min_element(ans.begin(), ans.end()) << std::endl;        return 0;}