#include <iostream>#include <map>#include <vector>#include <algorithm>int main(){    int n, a;    std::cin >> n >> a;    std::vector<int> x(n);    for(int i = 0; i < n; ++i){        std::cin >> x.at(i);    }    std::vector<std::vector<int>> dp(n + 1, std::vector<int>(a * n + 1, 0));    dp.at(0).at(0) = 1;    for(int i = 0; i < n; ++i){        for(int j = 0; j <= a * n; ++j){            if(j >= x.at(i)){                dp.at(i + 1).at(j) = dp.at(i).at(j - x.at(i)) + dp.at(i).at(j);            }else{                dp.at(i + 1).at(j) = dp.at(i).at(j);            }        }    }    std::cout << dp.at(n).at(a * n) << std::endl;    return 0;}