#include <iostream>#include <cstdlib>int main(){    std::string s;    std::cin >> s;    long long n = s.size(), ans = std::atoi(s.c_str());    for(int bits = 1; bits < (1 << n - 1); ++bits){        int i = 0, done = 0;        for(int j = 0; j < n - 1; ++j){            if(bits & (1 << j)){                ans += std::atoll(s.substr(n - 1 - j, j + 1 - i).c_str());                i = j + 1 - i;                done += i;            }        }                 ans += std::atoll(s.substr(0, n - done).c_str());    }    std::cout << ans << std::endl;    return 0;}