#include <iostream>#include <vector>int main(){    long long n;    std::cin >> n;    long t = 1;    long a = 1;    long tNext, aNext, tBuf, aBuf;    long i;    for(int j = 0; j < n; ++j){        std::cin >> tNext >> aNext;        if(0 == t % tNext){            tBuf = t / tNext;        }else{            tBuf = t / tNext + 1;        }        if(0 == a % aNext){            aBuf = a / aNext;        }else{            aBuf = a / aNext + 1;        }        i = std::max(tBuf, aBuf);        t = i * tNext;        a = i * aNext;    }    std::cout << t + a << std::endl;    return 0;}