#include <iostream>#include <vector>#define INF 1000000007;bool isPrime(int readVal){    switch(readVal){        case 0:            return false;        case 1:            return false;        case 2:            return true;    }    if(0 == readVal % 2){        return false;    }    for(int i = 3; i * i <= readVal; i += 2){        if(0 == readVal % i){            return false;        }    }    return true;}int main(){    int n;     std::cin >> n;    std::vector<int> vec;    for(int i = 2; i <= n; ++i){        if(isPrime(i)){            vec.push_back(i);        }    }    int sz = vec.size();    long long ans = 1;    for(int i = 0; i < sz ; ++i){        int count = 0;        for(int j = 1; j <= n; ++j){            int buf = j;            while(0 == buf % vec.at(i)){                ++count;                buf /= vec.at(i);            }        }        ans *= count + 1;        ans %= INF;    }    std::cout << ans << std::endl;    return 0;}