#include <iostream>#include <map>#include <vector>#include <algorithm>int main(){    long x;    std::cin >> x;    long ans = 0;    long i = 1;    long time = 0;    while(ans < x){        ans += ++i;        ++time;     }    if(ans == x){        std::cout << time << std::endl;    }else if(ans - x < time){        std::cout << time + 1 << std::endl;    }        return 0;}