#include <iostream>#include <map>#include <vector>#include <algorithm>int main(){    int n, shower;    std::cin >> n >> shower;    int ans = 0;    std::vector<int> t(n);    std::cin >> t.at(0);    for(int i = 1; i < n; ++i){        std::cin >> t.at(i);        if(t.at(i) - t.at(i - 1) < shower){            ans += t.at(i) - t.at(i - 1);        }else{            ans += shower;        }    }    std::cout << ans + shower << std::endl;    return 0;}