#include <iostream>#include <vector>#include <bitset>#include <cmath>int main(){    int n;    std::cin >> n;    std::vector<int> s(n);    for(int i = 0; i < n; ++i){        std::cin >> s.at(i);    }    std::vector<int> scores(n);    for(int i = 0; i < std::pow(2, n); ++i){        std::bitset<100> bs(i);        int val = 0;        for(int j = 0; j < n; ++j){            if(bs[j]){                val += s.at(j);            }//        std::cout << j << " ";        }//        std::cout << std::endl;;        if(0 != val % 10){            scores.push_back(val);        }//        std::cout << i << std::endl;    }    std::cout << *max_element(scores.begin(), scores.end()) << std::endl;    return 0;}