#include <iostream>#include <vector>#include <numeric>int main(){    int n;    std::cin >> n;    std::vector<int> s(n), t;    for(int i = 0; i < n; ++i){        std::cin >> s.at(i);        if(0 != s.at(i) % 10){            t.push_back(s.at(i));        }    }    int ans = std::accumulate(s.begin(), s.end(), 0);    if(0 == t.size()){        ans = 0;    }else if(0 == ans % 10){        ans -= *std::min_element(t.begin(), t.end());    }    std::cout << ans << std::endl;    return 0;}