#include <iostream>#include <vector>int main(){    int n;    std::cin >> n;    std::vector<int> list(9, 0);    for(int i = 0; i < n; ++i){        int val;        std::cin >> val;        int index = val / 400;        if(index < 8){            ++list.at(index);        }else{            ++list.at(8);        }    }    int min = std::count_if(list.begin(), list.begin() + 8, [](auto i){            return 0 < i;            });    std::cout << std::max(1, min) << " " << min + list.at(8) << std::endl;    return 0;}