#include <iostream>#include <vector>int main(){    int n;    std::cin >> n;    std::vector<int> list(9);    for(int i = 0; i < n; ++i){        int val;        std::cin >> val;        if(val < 400){            ++list.at(0);        }else if(val < 800){            ++list.at(1);        }else if(val < 1200){            ++list.at(2);        }else if(val < 1600){            ++list.at(3);        }else if(val < 2000){            ++list.at(4);        }else if(val < 2400){            ++list.at(5);        }else if(val < 2800){            ++list.at(6);        }else if(val < 3200){            ++list.at(7);        }else{            ++list.at(8);        }    }    int min = std::count_if(list.begin(), list.begin() + 8, [](int i){            return 0 < i;            });    std::cout << min << " " << min + list.at(8) << std::endl;    return 0;}