#include <iostream>#include <vector>void print(std::vector<int> readVec){    int sz = readVec.size();    for(int i = 0; i < sz; ++i){        std::cout << readVec.at(i);        if(sz - 1 != i){            std::cout << " ";        }    }    std::cout << std::endl;}int main(){    int n;    std::cin >> n;    std::vector<int> a(n);    for(int i = 0; i < n; ++i){        std::cin >> a.at(i);    }    std::vector<int> ans;        int i = n - 1, j = n % 2;    while(1 - j <= i){        ans.push_back(a.at(i));        i -= 2;    }    i = j;    while(i < n - 1){        ans.push_back(a.at(i));        i += 2;    }    print(ans);    return 0;}