#include <iostream>#include <vector>int main(){    int n, m;    std::cin >> n >> m;    std::vector<bool> fromOne(n + 1, false), toN(n + 1, false);    for(int i = 0; i < m; ++i){        int a, b;        std::cin >> a >> b;        if(a == 1 || b == 1){            fromOne.at(a + b - 1) = true;        }else if(a == n || b == n){            toN.at(a + b - n) = true;        }    }    std::string ans = "IMPOSSIBLE";    for(int i = 0; i <= n; ++i){        if(fromOne.at(i) && toN.at(i)){            ans = "POSSIBLE";            break;        }    }    std::cout << ans << std::endl;    return 0;}