#include <iostream>#include <vector>int main(){    int n;    std::cin >> n;    std::vector<int> a(n);    for(int i = 0; i < n; ++i){        std::cin >> a.at(i);    }    int numEven = std::count_if(a.begin(), a.end(), [](int i){            return 0 == i % 2;            });    int numFour = std::count_if(a.begin(), a.end(), [](int i){            return 0 == i % 4;            });    int numOdd  = std::count_if(a.begin(), a.end(), [](int i){            return 1 == i % 2;            });    bool flg1 = n == numEven;    bool flg2 = (std::abs(numFour - numOdd) <= 1) && (n == numFour + numOdd);    bool flg3 = (numFour == numOdd) && (n == numEven + numOdd);        std::string ans = "No";    if(flg1 || flg2 || flg3){        ans = "Yes";    }    std::cout << ans << std::endl;    return 0;}