#include <iostream>#include <cmath>long long gcd(long long a, long long b){    long long write;    if(0 == b){        write = a;    }else{        write = gcd(b, a % b);    }    return write;}long long lcm(long long a, long long b){    return a / gcd(a, b) * b;}int main(){    int n;    std::cin >> n;    long long ans = 1, t;    for(int i = 0; i < n; ++i){        std::cin >> t;        ans = lcm(ans, t);    }    std::cout << ans << std::endl;    return 0;}