#include <iostream>#include <cmath>#include <map>int main(){    int n;    std::cin >> n;    std::map<long long, long long> a;    for(int i = 0; i < n; ++i){        int val;        std::cin >> val;        auto itr = a.find(val);        if(a.end() == itr){            a.insert(std::make_pair(val, 1));        }else{            ++(itr->second);        }    }    long long sqr = 1;    auto ritr = a.rbegin();    ritr = std::find_if(ritr, a.rend(), [](auto i){        return 4 <= i.second;    });    if(a.rend() != ritr){        for(int i = 0; i < 2; ++i){            sqr *= ritr->first;        }    }else{        sqr = 0;    }    long long rec = 1;    ritr = a.rbegin();    for(int i = 0; i < 2; ++i){        ritr = std::find_if(ritr, a.rend(), [](auto i){            return 2 <= i.second;        });        if(a.rend() != ritr){            rec *= ritr->first;        }else{            rec = 0;            break;        }        ++ritr;    }    std::cout << std::max(sqr, rec) << std::endl;    return 0;}