#include <iostream>#include <vector>#include <algorithm>#include <cmath>void print(std::vector<int> readVec){    std::for_each(readVec.begin(), readVec.end(), [](int i){            std::cout << i << " ";            });    std::cout << std::endl;}int main(){    int n;    std::cin >> n;    std::vector<int> a(n);    for(int i = 0; i < n; ++i){        std::cin >> a.at(i);    }    std::sort(a.begin(), a.end());    print(a);    std::vector<int> b(n - 1);    for(int i = 0; i < n - 1; ++i){        b.at(i) = a.at(i + 1) - a.at(i);    }    print(b);    return 0;}