#include <iostream>#include <vector>#include <algorithm>#include <cmath>int main(){    int num = std::pow(10,5), n;    std::cin >> n;    std::vector<int> a(num + 2, 0);    for(int i = 0; i < n; ++i){        int val;        std::cin >> val;        ++a.at(val + 1);    }    int ans = 0;    for(int i = 1; i <= num; ++i){        ans = std::max(ans, a.at(i - 1) + a.at(i) + a.at(i + 1));    }    std::cout << ans << std::endl;    return 0;}