#include <iostream>#include <map>int main(){    int n;    std::cin >> n;    int val;    std::map<int, int> a;    for(int i = 0; i < n; ++i){        std::cin >> val;      	auto itr = a.find(val);      	if(itr != a.end()){	      	a.erase(itr);        }else{	      	a.insert(std::make_pair(val, 1));        }    }    std::cout << a.size() << std::endl;    return 0;}