#include <iostream>int main(){    std::string s;    std::cin >> s;    int a = (int)s.at(0), b = (int)s.at(1), c = (int)s.at(2), d = (int)s.at(3);    if(7 == a + b + c + d){        std::cout << a << "+" << b << "+" << c << "+" << d << "=7" << std::endl;    }else if(7 == a + b + c - d){        std::cout << a << "+" << b << "+" << c << "-" << d << "=7" << std::endl;    }else if(7 == a + b - c + d){        std::cout << a << "+" << b << "-" << c << "+" << d << "=7" << std::endl;    }else if(7 == a - b + c + d){        std::cout << a << "-" << b << "+" << c << "+" << d << "=7" << std::endl;    }else if(7 == a + b - c - d){        std::cout << a << "+" << b << "-" << c << "-" << d << "=7" << std::endl;    }else if(7 == a - b + c - d){        std::cout << a << "-" << b << "+" << c << "-" << d << "=7" << std::endl;    }else if(7 == a - b - c + d){        std::cout << a << "-" << b << "-" << c << "+" << d << "=7" << std::endl;    }else if(7 == a - b - c - d){        std::cout << a << "-" << b << "-" << c << "-" << d << "=7" << std::endl;    }    return 0;}