#include <iostream>#include <vector>#include <bitset>int main(){    std::string str;    std::cin >> str;    std::vector<int> s(4, 0);    for(int i = 0; i < 4; ++i){        s.at(i) = str.at(i) - 48;    }    std::string ops(3, '#');    int i = 0;    while(i < 8){        int result = s.at(0);        std::bitset<3> bs(i);        for(int j = 0; j < 3; ++j){            if(bs[2 - j]){                result += s.at(j + 1);                ops.at(j) = '+';            }else{                result -= s.at(j + 1);                ops.at(j) = '-';            }        }         if(7 == result){            break;        }        ++i;    }    std::cout << str.at(0);    for(int i = 0; i < 3; ++i){        std::cout << ops.at(i) << str.at(i + 1);    }    std::cout << "=7" << std::endl;    return 0;}