#include <iostream>#include <vector>#include <bitset>int main(){    std::string str;    std::cin >> str;    std::vector<int> s(4, 0);    for(int i = 0; i < 4; ++i){        s.at(i) = str.at(i) - 48;    }    int i = 0;    while(i < 8){        int result = s.at(0);        std::bitset<3> bs(i);            if(bs[0]){            result += s.at(3);        }else{            result -= s.at(3);        }        if(bs[1]){            result += s.at(2);        }else{            result -= s.at(2);        }        if(bs[2]){            result += s.at(1);        }else{            result -= s.at(1);        }        if(7 == result){            break;        }        ++i;    }    std::bitset<3> bs(i);    std::string ans = str.substr(0, 1);    if(bs[0]){        ans += "+";    }else{        ans += "-";    }    ans += str.substr(1, 1);    if(bs[1]){        ans += "+";    }else{        ans += "-";    }    ans += str.substr(2, 1);    if(bs[2]){        ans += "+";    }else{        ans += "-";    }    ans += str.substr(3, 1);    ans += "=7";    std::cout << ans << std::endl;    return 0;}