#include <iostream>#include <map>int main(){    int n;    std::cin >> n;        std::map<long long, long long> seq;    for(int i = 0; i < n; ++i){        int val;        std::cin >> val;        auto itr = seq.find(val);        if(seq.end() != itr){            ++itr->second;        }else{            seq.insert(std::make_pair(val, 1));        }    }    long long ans = 0;    for(auto itr = seq.begin(); itr != seq.end(); ++itr){        if(itr->second < itr->first){            ans += itr->second;        }else{            ans += itr->second - itr->first;        }    }    std::cout << ans << std::endl;    return 0;}