#include <iostream>int main(){    int n;    std::cin >> n;    bool flg = true;    for(int i = 0; i < n; ++i){        int t, x, y;        std::cin >> t >> x >> y;        if(t < x + y || 0 != (t - x - y) % 2){            flg = false;            break;        }    }    std::string ans = "Yes";    if(!flg){        ans = "No";    }    std::cout << ans << std::endl;    return 0;}