#include <iostream>#include <vector>#include <numeric>#include <algorithm>int main(){    int n;    std::cin >> n;    std::vector<std::vector<int>> a(2, std::vector<int> (n));    int r, c;    for(int i = 0; i < 2; ++i){        for(int j = 0; j < n; ++j){            std::cin >> a.at(i).at(j);        }    }    std::vector<int> candies(n, 0);    for(int i = 0; i < n; ++i){        candies.at(i) += std::accumulate(a.at(0).begin(), a.at(0).end() - i, 0);        candies.at(i) += std::accumulate(a.at(1).end() - (i + 1), a.at(1).end(), 0);    }    std::cout << *max_element(candies.begin(), candies.end()) << std::endl;    return 0;}