#include <iostream>#include <vector>int main(){    int n;    std::cin >> n;    std::vector<int> counts(5, 0);    for(int i = 0; i < n; ++i){        std::string str;        std::cin >> str;        switch(str.at(0)){            case 77 :                ++counts.at(0);                break;            case 65 :                ++counts.at(1);                break;            case 82:                 ++counts.at(2);                break;            case 67:                ++counts.at(3);                break;            case 72:                ++counts.at(4);                break;        }    }}