#include <iostream>#include <vector>int main(){    int n;    std::cin >> n;    std::vector<int> a(1, 0);    int sum = 0;    for(int i = 0; i < n; ++i){        int val;        std::cin >> val;        a.push_back(val);        sum += std::abs(val - a.at(i));    }    a.push_back(0);    sum += std::abs(a.at(n));    for(int i = 1; i <= n; ++i){        int val = std::abs(a.at(i) - a.at(i - 1)) + std::abs(a.at(i + 1) - a.at(i)) - std::abs(a.at(i + 1) - a.at(i - 1));        std::cout << sum - val << std::endl;    }    return 0;}