#include <iostream>#include <vector>#include <algorithm>int main(){    std::vector<int> num(3);    for(int i = 0; i < 3; ++i){        std::cin >> num.at(i);    }    std::sort(num.begin(), num.end());    int ans = num.at(2) - num.at(1) + (num.at(1) - num.at(0))/2;    if(1 == (num.at(1)- num.at(0)) % 2){        ans += 2;    }    std::cout << ans << std::endl;    return 0;}