#include <iostream>#include <vector>int main(){    int h, w;    std::cin >> h >> w;    std::vector<std::string> grid;    std::string dummy(w + 2, '$');    grid.push_back(dummy);    for(int i = 0; i < h; ++i){        std::string s;        std::cin >> s;        grid.push_back("$" + s + "$");    }    grid.push_back(dummy);    bool flg = true;    int i = 1;    while(flg && i < h - 1){        for(int j = 1; j < w - 1; ++j){            if('#' == grid.at(i).at(j)){                std::string buf =   grid.at(i - 1).substr(j, 1);                            buf +=  grid.at(i + 1).substr(j, 1);                            buf +=  grid.at(i).substr(j - 1, 1);                            buf +=  grid.at(i).substr(j + 1, 1);                if(std::string::npos == buf.find("#")){                    flg = false;                }            }        }        ++i;    }    std::string ans = "No";    if(flg){        ans = "Yes";    }    std::cout << ans << std::endl;    return 0;}