#include <iostream>const int INF = 1e9 + 7;int main(){    int n;    std::string str;    std::cin >> n >> str;            int min = INF;    for(int i = 0; i < n; ++i){        std::string refStr(i, 'E');        refStr += str.at(i);        refStr += std::string(n - 1 - i, 'W');        int count = 0;        for(int i = 0; i < n; ++i){            if(str.at(i) != refStr.at(i)){                ++count;            }        }        min = std::min(min, count);    }    std::cout << min << std::endl;    return 0;}